import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    cartList: []
  },
  mutations: {
    addCart (state, payload) {
      // payload新添加的商品
      let oldProduct = null
      // 判断购物车里面有没有在cartList里面存在
      for (let item in state.cartList) {
        if (item.iid === payload.iid) {
          oldProduct = item
        }
      }

    }
  }
})
