import { request, newRequest } from './request'

// 封装首页中所有的网络请求
export function getHomeMultidata () {
  return request({
    url: '/home/multidata'
  })
}


export function getHomeGoods () {
  return newRequest({
    url: '/home/data',
    params: {
      type,
      page
    }
  })

}
