import DetailNavBar from './childComps/DetailNavBar.vue';
import DetailCarousel from './childComps/DetailCarousel.vue';
import DetailShopInfo from './childComps/DetailShopInfo.vue';
import DetailBaseInfo from './childComps/DetailBaseInfo.vue';
import DetailGoodsInfo from './childComps/DetailGoodsInfo.vue';
import DetailParamInfo from './childComps/DetailParamInfo.vue';
import Scroll from 'components/common/scroll/Scroll';
import { getDetail, Goods, Shop, GoodsParam } from '@/network/detail';
export default {
  name: 'Detail',
  props: {},

  data() {
    return {
      iid: null,
      res: null,
      topImages: [],
      goods: {},
      shop: {},
      detailInfo: {},
      paramsInfo: {}
    };
  },

  created() {
    this.iid = this.$route.params.iid;
    getDetail(this.iid).then(res => {
      const {
        result
      } = res.data;
      this.topImages = result.itemInfo.topImages;
      console.log(result.itemInfo, result.columns, result.shopInfo.services); // 获取商品信息

      this.goods = new Goods(result.itemInfo, result.columns, result.shopInfo.services); // 创建店铺信息

      this.shop = new Shop(result.shopInfo);
      console.log(result.detailInfo); // 保存商品的详情数据

      this.detailInfo = result.detailInfo; // 获取参数信息

      this.paramsInfo = new GoodsParam(result.itemParams.info, result.itemParams.rule);
    });
  },

  mounted() {},

  methods: {
    imageLoad() {
      this.$refs.scroll.refresh();
    }

  },
  computed: {},
  components: {
    DetailNavBar,
    DetailCarousel,
    DetailBaseInfo,
    DetailShopInfo,
    DetailGoodsInfo,
    Scroll
  }
};
