import BScroll from '@better-scroll/core';
import Pullup from '@better-scroll/pull-up';
BScroll.use(Pullup);
export default {
  name: 'Scroll',
  props: {},

  data() {
    return {
      bscroll: null,
      msg: 123
    };
  },

  methods: {
    initBscroll() {
      this.bscroll = new BScroll(this.$refs.wrapper, {
        pullUpLoad: true,
        click: true
      });
      this.bscroll.on('pullingUp', this.pullingUpHandler); // 监听滚动位置
    },

    async pullingUpHandler() {
      this.isPullUpLoad = true;
      await this.requestData();
      this.bscroll.finishPullUp();
      this.bscroll.refresh();
      this.isPullUpLoad = false;
    },

    async requestData() {
      try {
        const newData = await this.ajaxGet();
        this.data += newData;
      } catch (err) {
        // handle err
        console.log(err);
      }
    },

    ajaxGet() {
      return new Promise(resolve => {
        setTimeout(() => {
          resolve(20);
        }, 1000);
      });
    }

  },

  mounted() {
    this.initBscroll();
  },

  created() {},

  computed: {},
  components: {}
};
