import HomeRecommendView from './childComps/HomeRecommendView';
import HomeFeature from './childComps/HomeFeature';
import NavBar from 'components/common/navbar/NavBar';
import Scroll from 'components/common/scroll/Scroll';
import TabControl from 'components/content/tabControl/TabControl';
import Carousel from 'components/content/Carousel/Carousel';
import GoodsList from 'components/content/goods/GoodsList';
import { getHomeMultidata, getHomeGoods } from '@/network/home';
import BScroll from '@better-scroll/core';
import Pullup from '@better-scroll/pull-up';
BScroll.use(Pullup);
export default {
  name: 'Home',

  data() {
    return {
      banner: [],
      recommends: [],
      currentPage: 'pop',
      goods: {
        'pop': {
          page: 0,
          list: []
        },
        'new': {
          page: 0,
          list: []
        },
        'sell': {
          page: 0,
          list: []
        }
      }
    };
  },

  // 组件创建完成发送网络请求
  created() {
    // 请求多个数据
    this.getHomeMultidata(); // 请求商品数据

    this.getHomeGoods("pop");
    this.getHomeGoods("new");
    this.getHomeGoods("sell");
  },

  methods: {
    /**
     * 网络请求相关方法
     */
    getHomeMultidata() {
      getHomeMultidata().then(res => {
        const {
          data
        } = res.data;
        this.banner = data.banner.list;
        this.recommends = data.recommend.list;
      });
    },

    getHomeGoods(type) {
      const page = this.goods[type].page + 1;
      getHomeGoods(type, page).then(res => {
        const {
          data
        } = res.data;
        this.goods[type].list.push(...data.list);
        this.goods[type].page + 1;
      });
    },

    /**
     * 事件监听相关
     */
    tabClick(index) {
      // console.log(index)
      switch (index) {
        case 0:
          this.currentPage = 'pop';
          break;

        case 1:
          this.currentPage = 'new';
          break;

        case 2:
          this.currentPage = 'sell';
          break;
      }
    }

  },
  computed: {
    showGoods() {
      return this.goods[this.currentPage].list;
    }

  },
  components: {
    HomeRecommendView,
    HomeFeature,
    NavBar,
    Scroll,
    TabControl,
    Carousel,
    GoodsList
  }
};
