// 防抖函数, 使用场景：调用的函数比较频繁的话
export const debounce = (func, delay) => {
  let timer = null
  return (...args) => {
    if (timer) clearTimeout(timer)
    timer = setTimeout(() => {
      func.apply(this, args)
    }, delay)
  }
}
