import { request } from './request'
// 封装首页中所有的网络请求
export function getHomeMultidata () {
  function gethome () {
    const instance = axios.create({
      baseURL: 'http://106.54.54.237:8000',
      timeout: 5000,
    })
    return instance(config)
  }
  return gethome({
    url: '/home/multidata'
  })
}

export function getHomeGoods () {
  return request({
    url: '/home/data',
    params: {
      type,
      page
    }
  })

}
