import { ADD_COUNTER, ADD_TO_CART } from './mutations-type'

export default {
  // mutations里面的方法尽可能单一
  [ADD_COUNTER] (state, payload) {
    payload.count += 1
  },
  [ADD_TO_CART] (state, payload) {
    console.log(payload)
    payload.checked = true
    payload.count = 1
    state.cartList.push(payload)
  }
}
